import React from 'react'
import data from './drugs.js'
import { FaPlus } from 'react-icons/fa'

class PharmacoTool extends React.Component{
    constructor(props){
        super(props);
        this.clickNumber = 0
        this.state = {
            dci:[],
            ddmt:''
        }
        this.handlchange = this.handlchange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.didDciMatch = this.didDciMatch.bind(this)
        this.addDrug = this.addDrug.bind(this)
        this.test = this.test.bind(this)
    }
    handlchange(event){
        const {name ,value } = event.target
        if (name == "dci"){
            this.setState({
                [name]:this.state.dci.push(value)
            })
        }
        this.setState({
            [name]:value
        })
        this.didDciMatch(this.state.dci)
    }
    test(){
        console.log('Event Worked !')
    }
    handleSubmit(event){
        event.preventDefault();
        console.log(JSON.stringify(this.state))
    }
    addDrug(event){
        this.clickNumber = this.clickNumber + 1;
        const hostElem = document.getElementsByClassName('meds-name')[0]
        const newInput = document.createElement('input')
        newInput.setAttribute('name','dci')
        newInput.setAttribute('value',this.state.dci.push(this.state.dci))
        newInput.onchange = this.test
        newInput.setAttribute('placeholder','   Name of the Drug')
        newInput.classList.add('pharmaInput')
        hostElem.appendChild(newInput)

    }
    didDciMatch(mdci){
        //console.log(mdci)
    }
    render(){
        return(
        <div className={this.props.status?"pharma raised":'hide raised'}>
            <section className="meds-name">
                <h4 style={{'paddingTop':20}}>Medicaments</h4>
                <input 
                type="text"
                name="dci"
                value={this.state.dci}
                required={true}
                onChange={this.handlchange}
                placeholder="   Name of the Drug"
                className="pharmaInput"
                />
                <br></br>
                <FaPlus 
                className="med-add-button"
                onClick={this.addDrug}
                />
            </section>
            <div>
            {this.state.dci}
            </div>
            <section className="side-effects">
            <h4 style={{'paddingTop':20}}>Effects Indesirables</h4>
                <textarea 
                name="effects"
                type="text"
                required={true}
                placeholder="   Side Effect"
                className="pharmaInput"
                />
            </section>
            <section className="meds-info">
                <form id="pharmatoolin" onSubmit={this.handleSubmit} method="POST">
                    <h5 style={{paddingTop:'40px'}}>Information</h5>
                    <input 
                    type='text'
                    placeholder='   Posology'
                    className="pharmaInput"
                    required={true}
                    /><br></br>
                    <input 
                    type='text'
                    placeholder='   Voie'
                    className="pharmaInput"
                    required={true}
                    /><br></br>
                    <input 
                    type='number'
                    placeholder='   NÂ°lot'
                    className="pharmaInput"
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DAEI'
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DEM'
                    /><br></br>
                    <input 
                    value={this.state.ddmt}
                    name='ddmt'
                    type='date'
                    onChange={this.handlchange}
                    placeholder='DDMT'
                    /><br></br>
                    <button id="btn">Submit</button>
           </form>
        </section>
        </div>
        )
    }
}
export default PharmacoTool;
