import React from 'react'
import data from './drugs.js'
import { FaPlus } from 'react-icons/fa'

class PharmacoTool extends React.Component{
    constructor(props){
        super(props);
        this.clickNumber = 0
        this.state = {
            currentDrugName:'',
            list:[{text:" Name of the drug"}],
            ddmt:''
        }
        this.handlchange = this.handlchange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.didDciMatch = this.didDciMatch.bind(this)
        this.addDrug = this.addDrug.bind(this)
    }
    handlchange(event){
        const {name ,value } = event.target
        if (name === "drugs"){
            this.setState(state =>{
                const drugs = state.drugs.concat([state.currentDrugName])
                return {
                    drugs
                }
            })
        }
        else{
            this.setState({
                [name]:value
            })
        }

        this.didDciMatch(this.state.drugs)

    }
    handleSubmit(event){
        event.preventDefault();
        console.log(JSON.stringify(this.state))
    }
    addDrug(event){
        this.state.list.push({ text: "" });
        this.setState([...this.state.list]);
        console.log(this.state.list.length)

    }
    didDciMatch(mdrugs){
        console.log(this.state.drugs)
    }
    render(){
        return(
        <div className={this.props.status?"pharma raised":'hide raised'}>
            <section className="meds-name">
                <h4 style={{'paddingTop':20}}>Medicaments</h4>
               { /* <input 
                type="text"
                name="drugs"
                value={this.state.currentDrugName}   
                required={true}
                onChange={this.handlchange}
                placeholder="   Name of the Drug"
                className="pharmaInput"
               /> */ }
                {this.state.list.map((n, i) => (
            <input
            required={true}
            className="pharmaInput"
            key={i}
            value={n.text}
            onChange={e => {
              this.state.list[i].text = e.target.value;
              setList([...this.state.list]);
            }}
            />
            ))}
                <br></br>
                <FaPlus 
                className="med-add-button"
                onClick={this.addDrug}
                />
            </section>
            <section className="side-effects">
            <h4 style={{'paddingTop':20}}>Effects Indesirables</h4>
                <textarea 
                name="effects"
                type="text"
                required={true}
                placeholder="   Side Effect"
                className="pharmaInput"
                />
            </section>
            <section className="meds-info">
                <form id="pharmatoolin" onSubmit={this.handleSubmit} method="POST">
                    <h5 style={{paddingTop:'40px'}}>Information</h5>
                    <input 
                    type='text'
                    placeholder='   Posology'
                    className="pharmaInput"
                    required={true}
                    /><br></br>
                    <input 
                    type='text'
                    placeholder='   Voie'
                    className="pharmaInput"
                    required={true}
                    /><br></br>
                    <input 
                    type='number'
                    placeholder='   NÂ°lot'
                    className="pharmaInput"
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DAEI'
                    /><br></br>
                    <input 
                    type='date'
                    placeholder='DEM'
                    /><br></br>
                    <input 
                    value={this.state.ddmt}
                    name='ddmt'
                    type='date'
                    onChange={this.handlchange}
                    placeholder='DDMT'
                    /><br></br>
                    <button id="btn">Submit</button>
           </form>
        </section>
        </div>
        )
    }
}
export default PharmacoTool;
